function palindromeNum(n, m) {
    var res = [];
    function num(n, m) {
        res.push(n);
        if (n < m) {
            num(n + 1, m);
            res.push(n);
        }
    }
    num(2, 5);
    return res.join('');
};
palindromeNum(2,5); //2345432
```

