``` javascript
const NotificationInstance = Notification || window.Notification;
if (!!NotificationInstance) {
    const permissionNow = NotificationInstance.permission;
    if (permissionNow === 'granted') {//
        CreatNotification();
    } else if (permissionNow === 'denied') {
        console.log('!!!');
    } else {
        setPermission();
    }
    function setPermission() {
        //
        NotificationInstance.requestPermission(function (PERMISSION) {
            if (PERMISSION === 'granted') {
                CreatNotification();
            } else {
                console.log('!!!');
            }
        });
    }
    function CreatNotification() {
        const n = new NotificationInstance('XX', {
            body: '',
            tag: '2ue',
            icon: 'https://2ue.github.io/images/common/avatar.png',
            data: {
                url: 'https://2ue.github.io'
            }
        });
        n.onshow = function () {
            console.log('');
        }
        n.onclick = function (e) {
            //data
            //edata
            window.open(n.data.url, '_blank');
            n.close();
        }
        n.onclose = function () {
            console.log('');
        }
        n.onerror = function (err) {
            console.log('');
            throw err;
        }
        setTimeout(() => {
            n.close();
        }, 2000);
    }
}
```

