'use strict';
var gulp = require('gulp'),
    less = require('gulp-less'),
    webpack = require("gulp-webpack"),
    webpackConfig = require("./webpack.config.js");

var isDev = true;
var entrySrc = 'src/'
var pathSrc = isDev ? 'dist/' : 'online/';

