    gulp.src([entrySrc + 'css/main.less', entrySrc + 'css/*.css'])
        .pipe(less())
        .pipe(concat('style.min.css'))
        .pipe(gulp.dest(pathSrc + 'css/'))
        .on('end', done);
});

//gulpwebpack.config.js
gulp.task('buildJs', function () {
    var myConfig = Object.create(webpackConfig);
